// Parse token
const data = JSON.parse(msg.payload);

const currentToken = flow.get("currentToken");
const tokenTimestamp = flow.get("tokenTimestamp");
const now = new Date().getTime();
const threeMinutes = 10 * 60 * 1000;

if (data.token) {
    if (!currentToken || (now - tokenTimestamp > threeMinutes) || data.token !== currentToken) {
        flow.set("currentToken", data.token);
        flow.set("tokenTimestamp", now);
        flow.set("allow", true);
    } else {
        flow.set("allow", true);
    }
} else {
    flow.set("allow", false);
}

return null;

const tokenTimestamp = flow.get("tokenTimestamp") || 0;
const now = new Date().getTime();
const threeMinutes = 10 * 60 * 1000;

if (now - tokenTimestamp > threeMinutes) {
    flow.set("allow", false);
    return { payload: "redirect" };
}

return null;

const text = msg.payload.toLowerCase();  // chuyển về chữ thường

// Chỉ xử lý khi có chữ "tắt" hoặc "tắc"
if (text.includes("bật") || text.includes("mở")) {
    const matches = text.match(/\d+/g); // tìm tất cả các số

    if (matches && matches.length > 0) {
        const messages = matches.map(number => {
            return {
                payload: {
                    device: parseInt(number),
                    Hour: 1
                }
            };
        });

        // Trả về nhiều tin nhắn (sử dụng node "Split" để tách hoặc return [msg1, msg2, ...])
        return [messages];
    }
}

// Nếu không có chữ "tắt" hoặc không có số thì bỏ qua
