const text = msg.payload.toLowerCase();

// Kiểm tra các từ khóa chỉ toàn bộ
const tatHet = text.includes("tắt hết") || text.includes("tắt tất cả") || text.includes("tắt toàn bộ");

if (tatHet) {
    // Danh sách các thiết bị giả sử là 1 -> 3
    const allDevices = Array.from({ length: 70 }, (_, index) => index + 1);

    const messages = allDevices.map(number => ({
        payload: {
            device: number,
            Hour: 1
        }
    }));

    return [messages];
}



return null;

const status = msg.payload.status;
const value = msg.payload.value;

msg.payload = {
    label: "Đèn " + value,
    status: status,
    color: status === "ON"
        ? "linear-gradient(135deg, #00ffe1, #0072ff)"
        : "#2e2e2e", // Màu nền khi tắt
    shadow: status === "ON"
        ? "0 0 10px rgba(0, 255, 225, 0.6)"
        : "0 0 5px rgba(0, 255, 225, 0.15)" // viền xanh neon mờ khi tắt
};

return msg;

let input = ""; // hoặc: { payload: "" }

// Xóa khỏi flow context
flow.set("device_num", "");


// Trả về 2 output: 1 -> MQTT, 2 -> reset UI
return [[{ payload: input }]];

let input = ""; // hoặc: { payload: "" }

// Xóa khỏi flow context
flow.set("device_num", "");


// Trả về 2 output: 1 -> MQTT, 2 -> reset UI
return [[{ payload: input }]];

// Mặc định 70 đèn OFF và xám đậm
let leds = global.get("leds") || Array.from({ length: 70 }, (_, i) => ({
    id: i + 1,
    status: "OFF",
    color: "#444"
}));

if (msg.payload && msg.payload.value !== undefined) {
    const index = msg.payload.value - 1;
    if (index >= 0 && index < 70) {
        leds[index].status = msg.payload.status;
        leds[index].color = msg.payload.status === "ON" ? "#00c9ff" : "#444";
    }
}

global.set("leds", leds);
msg.payload = leds;
return msg;

let now = new Date();

// Cộng thêm 7 tiếng để ra giờ Việt Nam
now.setHours(now.getHours() + 7);

let gio = now.getHours().toString().padStart(2, '0');
let phut = now.getMinutes().toString().padStart(2, '0');
let giay = now.getSeconds().toString().padStart(2, '0');

let ngay = now.getDate().toString().padStart(2, '0');
let thang = (now.getMonth() + 1).toString().padStart(2, '0');
let nam = now.getFullYear();

msg.payload = `🕒 ${gio}:${phut}:${giay} | 📅 ${ngay}/${thang}/${nam}`;
return msg;

if (msg.payload === "ALL_OFF") {
    const msgs = [];
    for (let i = 1; i <= 70; i++) {
        msgs.push({
            payload: {
                device: i,
                Hour: 1
            }
        });
    }

    // Gửi ngay lập tức
    node.send([msgs]);

    // Sau 30 giây, gửi lại y hệt
    setTimeout(() => {
        node.send([msgs]);
    }, 20000); // 30 giây = 30000 ms

    // Trả về null vì đã dùng node.send()
    return null;
}

return [null];

if (msg.payload === "ALL_ON") {
    const msgs = [];
    for (let i = 1; i <= 70; i++) {
        msgs.push({
            payload: {
                device: i,
                Hour: 1
            }
        });
    }

    // Gửi ngay lập tức
    node.send([msgs]);

    // Gửi lại sau 30 giây
    setTimeout(() => {
        node.send([msgs]);
    }, 20000); // 30 giây = 30000 ms

    // Không return gì vì đã dùng node.send()
    return null;
}

return [null];

node.send({ payload: msg.payload }); // Gửi lần 1

setTimeout(() => {
    node.send({ payload: msg.payload }); // Gửi lần 2 sau 1 phút
}, 60 * 1000);

return null;

// Parse token
const data = JSON.parse(msg.payload);

// Đổi tên biến token và timestamp
const currentToken = flow.get("myNewToken");
const tokenTimestamp = flow.get("myNewTokenTimestamp");

const now = new Date().getTime();
const tenMinutes = 10 * 60 * 1000;

if (data.token) {
    if (!currentToken || (now - tokenTimestamp > tenMinutes) || data.token !== currentToken) {
        flow.set("myNewToken", data.token);
        flow.set("myNewTokenTimestamp", now);
        flow.set("mic", true);
    } else {
        flow.set("mic", true);
    }
} else {
    flow.set("mic", false);
}

return null;

