const text = msg.payload.toLowerCase();  // chuyển về chữ thường

// Chỉ xử lý khi có chữ "tắt" hoặc "tắc"
if (text.includes("bật") || text.includes("mở")) {
    const matches = text.match(/\d+/g); // tìm tất cả các số

    if (matches && matches.length > 0) {
        const messages = matches.map(number => {
            return {
                payload: {
                    device: parseInt(number),
                    Hour: 1
                }
            };
        });

        // Trả về nhiều tin nhắn (sử dụng node "Split" để tách hoặc return [msg1, msg2, ...])
        return [messages];
    }
}

// Nếu không có chữ "tắt" hoặc không có số thì bỏ qua
