const status = msg.payload.status;
const value = msg.payload.value;

msg.payload = {
    label: "Đèn " + value,
    status: status,
    color: status === "ON"
        ? "linear-gradient(to right, #00c6ff, #0072ff)"  // gradient xanh ngọc -> xanh dương
        : "#2c2c2c"  // xám đậm khi OFF
};

return msg;

